// import React from "react";

const merge = (leftArr, rightArr) => {
  var result = [];

  while (leftArr.length > 0 && rightArr.length > 0)
    result.push(leftArr[0] < rightArr[0] ? leftArr.shift() : rightArr.shift());

  return result.concat(leftArr.length ? leftArr : rightArr);
};

function mergeSort(arr, animations) {
  if (arr === null) {
    return;
  }

  if (arr.length === 1) {
    return arr;
  }

  const midIndex = Math.floor(arr.length / 2);
  const leftArr = mergeSort(arr.slice(0, midIndex), animations);
  const rightArr = mergeSort(arr.slice(midIndex), animations);

  return merge(mergeSort(leftArr, animations), mergeSort(rightArr, animations));
}

export function SortAnimations(algoName, arr) {
  const animations = [];
  const tempArray = array.slice();
  const mergeSortObj = {
    animations: null,
    sortedArray: null,
  };

  switch (algoName) {
    case "Merge Sort":
      mergeSortObj.sortedArray = mergeSort(arr);
      mergeSortObj.animations = mergeSortHelper(
        arr,
        0,
        arr.length - 1,
        tempArray,
        animations
      );
  }

  return mergeSortObj;
}

function mergeSortHelper(arr, startIdx, endIdx, tempArray, animations) {
  if (startIdx === endIdx) return;
  const middleIdx = Math.floor((startIdx + endIdx) / 2);
  mergeSortHelper(tempArray, startIdx, middleIdx, arr, animations);
  mergeSortHelper(tempArray, middleIdx + 1, endIdx, arr, animations);
  doMerge(arr, startIdx, middleIdx, endIdx, tempArray, animations);
}

function doMerge(arr, startIdx, middleIdx, endIdx, tempArray, animations) {
  let k = startIdx;
  let i = startIdx;
  let j = middleIdx + 1;
  while (i <= middleIdx && j <= endIdx) {
    animations.push([i, j]);
    animations.push([i, j]);
    if (tempArray[i] <= tempArray[j]) {
      animations.push([k, tempArray[i]]);
      arr[k++] = tempArray[i++];
    } else {
      animations.push([k, tempArray[j]]);
      arr[k++] = tempArray[j++];
    }
  }
  while (i <= middleIdx) {
    animations.push([i, i]);
    animations.push([i, i]);
    animations.push([k, tempArray[i]]);
    arr[k++] = tempArray[i++];
  }
  while (j <= endIdx) {
    animations.push([j, j]);
    animations.push([j, j]);
    animations.push([k, tempArray[j]]);
    arr[k++] = tempArray[j++];
  }
}
