import React, { useState, useRef, useEffect } from 'react'
import VerticalBar from '../ui/VerticalBar';
import { SortAnimations } from '../../utils/SortingAlgorithms';

export default function SortingVizualizer() {
    const [initialArray, setInitialArray] = useState(null);
    const [newArray, setNewArray] = useState(null);
    const [width, setWidth] = useState(0);
    const [activeAlgoName, setActiveAlgoName] = useState("N/A")
    const ref = useRef(null);

    const generateNewArray = () => {
        const curArray = [];

        setNewArray(null);

        for (let i = 0; i < 100; i++) {
            curArray.push(randomIntFromInterval(5, 500));
        }
        setInitialArray(curArray);
    };

    const mergeSort = () => {
        const { sortedArray, animations } = SortAnimations('Merge Sort', initialArray);
        setNewArray(sortedArray);
        setActiveAlgoName('Merge Sort');

        for (let i = 0; i < animations.length; i++) {
            const verticalBars = document.getElementsByClassName('vertical-bars');
            const onColorChange = i % 3 !== 2;
            const color = i % 3 === 0 ? 'red' : 'blue';

            if (onColorChange) {
                const [oneIdx, twoIdx] = animations[i];
                const oneColor = verticalBars[oneIdx].color;
            }
        }
    };

    const quickSort = () => {

    };

    const heapSort = () => {

    };

    const bubbleSort = () => {

    };

    const testSortingAlgorithm = () => {
        const jsSortedArr = initialArray.toSorted((a, b) => a - b);

        if (activeAlgoName === "N/A") {
            return console.log('No active sorting. Choose a sorting algorithm.')
        }

        if (arraysAreEqual(newArray, jsSortedArr)) {
            console.log(activeAlgoName + ': PASSED')
        }
    };

    useEffect(() => {
        setWidth(ref.current.offsetWidth);

        const getwidth = () => {
            setWidth(ref.current.offsetWidth / 100);
        };

        window.addEventListener("resize", getwidth);

        return () => window.removeEventListener("resize", getwidth);
    }, []);

    return (
        <div className='flex place-items-center min-h-screen flex-col p-20'>
            <div className='w-5/6 min-w-96 h-[515px]' ref={ref}>
                {initialArray === null ? (
                    <div>Generate a New Array.</div>
                ) :
                    <div className='flex flex-row place-items-end'>
                        {newArray === null ?
                            (initialArray.map((int, idx) =>
                                <VerticalBar key={idx} width={width} height={int} />
                            )) :
                            (newArray.map((int, idx) =>
                                <VerticalBar className='vertical-bars' key={idx} width={width} height={int} color="red" />
                            ))
                        }
                    </div>
                }
            </div>
            <div className="flex flex-row gap-2 m-5 text-white text-sm">
                <button className="py-1 px-2 bg-blue-950 rounded-md" onClick={generateNewArray}>Generate New Array</button>
                <button className="py-1 px-2 bg-blue-950 rounded-md" onClick={mergeSort}>Merge Sort</button>
                <button className="py-1 px-2 bg-blue-950 rounded-md" onClick={quickSort}>Quick Sort</button>
                <button className="py-1 px-2 bg-blue-950 rounded-md" onClick={heapSort}>Heap Sort</button>
                <button className="py-1 px-2 bg-blue-950 rounded-md" onClick={bubbleSort}>Bubble Sort</button>
                <button className="py-1 px-2 bg-blue-950 rounded-md" onClick={testSortingAlgorithm}>Test Algorithm</button>
            </div>
        </div >
    )
}

// From https://stackoverflow.com/questions/4959975/generate-random-number-between-two-numbers-in-javascript
function randomIntFromInterval(min, max) {
    return Math.floor(Math.random() * (max - min + 1) + min);
}

function arraysAreEqual(arrOne, arrTwo) {
    if (arrOne.length !== arrTwo.length) return false;

    for (let i = 0; i < arrOne.length; i++) {
        if (arrOne[i] !== arrTwo[i]) return false;
    }

    return true;
}
